export default function Sidebar({ open, onClose }) {
    return (
      <>
        {/* ✅ BACKDROP */}
        {open && (
          <div
            onClick={onClose}
            style={{
              position: "fixed",
              top: 0,
              left: 0,
              width: "100vw",
              height: "100vh",
              background: "rgba(0,0,0,0.4)",
              zIndex: 999
            }}
          />
        )}

        {/* ✅ SIDEBAR PANEL */}
        <div
          style={{
            position: "fixed",
            top: 0,
            left: open ? 0 : "-260px",   // ✅ FULLY OFF SCREEN WHEN CLOSED
            width: 260,
            height: "100vh",
            background: "#5b9491",      // ✅ ORIGINAL COLOR (NO DARK MODE)
            color: "white",
            padding: 20,
            transition: "left 0.3s ease-in-out", // ✅ SMOOTH SLIDE
            zIndex: 1000,
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between"
          }}
        >
          <div>
            <h2 style={{ marginBottom: 20 }}>Menu</h2>

            <button style={btnStyle}>Home</button>
            <button style={btnStyle}>Profile</button>
            <button style={btnStyle}>Leaderboard</button>
          </div>

          {/* ✅ BOTTOM BUTTON FIXED (NO MORE CUT OFF) */}
          <div>
            <button
              style={{
                width: "100%",
                padding: 12,
                background: "#ffffff",
                color: "#5b9491",
                border: "none",
                borderRadius: 6,
                fontWeight: "bold",
                cursor: "pointer"
              }}
            >
              Logout
            </button>
          </div>
        </div>
      </>
    );
  }

  const btnStyle = {
    width: "100%",
    padding: 12,
    marginBottom: 10,
    background: "white",
    color: "#5b9491",
    border: "none",
    borderRadius: 6,
    fontWeight: "bold",
    cursor: "pointer"
  };
