import React, { useEffect, useState } from "react";
import { fetchQuestions } from "../services/questionService";
import { submitScore } from "../services/scoreService";
import QuestionCard from "../components/QuestionCard";
import HintButton from "../components/HintButton";

export default function GamePage() {
  const [questions, setQuestions] = useState([]);
  const [index, setIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [hintUsed, setHintUsed] = useState(false);
  const [hiddenOptions, setHiddenOptions] = useState([]);

  useEffect(() => {
    async function load() {
      const data = await fetchQuestions();
      setQuestions(data);
    }
    load();
  }, []);

  function handleAnswer(choice) {
    const correctAnswer = questions[index].answer;

    if (choice === correctAnswer) {
      setScore((prev) => prev + 1);
    }

    setHintUsed(false);
    setHiddenOptions([]);

    if (index + 1 < questions.length) {
      setIndex((prev) => prev + 1);
    } else {
      submitScore(score + (choice === correctAnswer ? 1 : 0));
      setGameOver(true);
    }
  }

  function useHint() {
    if (hintUsed) return;

    const correct = questions[index].answer;
    let removed = [];

    while (removed.length < 2) {
      const r = Math.floor(Math.random() * 4);
      if (r !== correct && !removed.includes(r)) {
        removed.push(r);
      }
    }

    setHiddenOptions(removed);
    setHintUsed(true);
  }

  if (questions.length === 0) {
    return <div style={{ padding: 30 }}>Loading questions...</div>;
  }

  if (gameOver) {
    return (
      <div style={{ padding: 30 }}>
        <h1>Game Over ðŸŽ‰</h1>
        <h2>Your Score: {score}</h2>
      </div>
    );
  }

  return (
    <div style={{ padding: 30 }}>
      <h1>Braintease Game</h1>
      <h3>Score: {score}</h3>

      <HintButton onHint={useHint} disabled={hintUsed} />

      <QuestionCard
        question={questions[index]}
        onAnswer={handleAnswer}
        hiddenOptions={hiddenOptions}
      />
    </div>
  );
}
