export default function Sidebar({ open, onClose }) {
    return (
      <>
        {/* Overlay */}
        {open && (
          <div
            onClick={onClose}
            style={{
              position: "fixed",
              inset: 0,
              background: "rgba(0,0,0,0.4)",
              zIndex: 999
            }}
          />
        )}

        {/* Sidebar */}
        <div
          style={{
            position: "fixed",
            top: 0,
            left: open ? 0 : "-300px", // ✅ FORCED FULL HIDE
            width: 300,
            height: "100vh",
            background: "#111",
            color: "white",
            padding: 20,
            transition: "left 0.25s ease-in-out",
            zIndex: 1000,
            display: "flex",
            flexDirection: "column",
            gap: 20
          }}
        >
          <h2>Menu</h2>

          <button style={btn}>Home</button>
          <button style={btn}>Play</button>
          <button style={btn}>Leaderboard</button>
          <button style={btn}>Profile</button>

          <div style={{ marginTop: "auto" }}>
            <button style={logout}>Logout</button>
          </div>
        </div>
      </>
    );
  }

  const btn = {
    background: "none",
    border: "none",
    color: "white",
    fontSize: 18,
    textAlign: "left",
    cursor: "pointer"
  };

  const logout = {
    background: "#ff4d4d",
    border: "none",
    padding: 10,
    borderRadius: 6,
    color: "white",
    fontWeight: "bold",
    cursor: "pointer"
  };
