export default function HintButton({ onHint, disabled }) {
    return (
      <button
        onClick={onHint}
        disabled={disabled}
        style={{
          marginBottom: 20,
          padding: 10,
          background: disabled ? "#999" : "#f4c542",
          border: "none",
          cursor: "pointer"
        }}
      >
        {disabled ? "Hint Used" : "Use Hint"}
      </button>
    );
  }
