import { useState } from "react";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  function handleSubmit(e) {
    e.preventDefault();

    if (password.length < 6) {
      setError("Password must be at least 6 characters");
      return;
    }

    setError("");
    alert("Login pressed (auth is disabled for now)");
  }

  return (
    <div style={styles.page}>
      <div style={styles.back}>‚Üê Back</div>

      <div style={styles.card}>
        <h2>Sign In</h2>
        <p style={styles.subtitle}>Enter your credentials to continue</p>

        {error && <div style={styles.error}>{error}</div>}

        <form onSubmit={handleSubmit}>
          <label>Email</label>
          <div style={styles.inputWrap}>
            <span>‚úâÔ∏è</span>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              required
            />
          </div>

          <label>Password</label>
          <div style={styles.inputWrap}>
            <span>üîí</span>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              required
            />
          </div>

          <button style={styles.signInBtn}>Sign In</button>
        </form>

        <div style={styles.divider}>Or continue with</div>

        <button style={styles.googleBtn}>
          <span>G</span> Sign in with Google
        </button>

        <p style={styles.footer}>
          Don&apos;t have an account? <span style={styles.link}>Sign up</span>
        </p>
      </div>
    </div>
  );
}

const styles = {
  page: {
    minHeight: "100vh",
    background: "#f5f7fb",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    position: "relative"
  },

  back: {
    position: "absolute",
    top: 20,
    left: 20,
    fontSize: 16,
    color: "#5b918c",
    cursor: "pointer"
  },

  card: {
    width: 360,
    background: "white",
    padding: 30,
    borderRadius: 12,
    boxShadow: "0px 10px 30px rgba(0,0,0,0.1)"
  },

  subtitle: {
    color: "#777",
    marginBottom: 20
  },

  error: {
    background: "#fdecea",
    color: "#b91c1c",
    padding: 10,
    borderRadius: 6,
    marginBottom: 15,
    fontSize: 14
  },

  inputWrap: {
    display: "flex",
    alignItems: "center",
    gap: 8,
    background: "#f1f5fb",
    padding: "10px 12px",
    borderRadius: 8,
    marginBottom: 15
  },

  signInBtn: {
    width: "100%",
    background: "#5b918c",
    color: "white",
    border: "none",
    padding: "12px 0",
    borderRadius: 8,
    marginTop: 10,
    fontWeight: "bold",
    cursor: "pointer"
  },

  divider: {
    textAlign: "center",
    margin: "20px 0",
    color: "#aaa",
    fontSize: 14
  },

  googleBtn: {
    width: "100%",
    padding: "10px 0",
    borderRadius: 8,
    border: "1px solid #ddd",
    background: "white",
    fontWeight: "bold",
    cursor: "pointer"
  },

  footer: {
    textAlign: "center",
    marginTop: 20,
    fontSize: 14
  },

  link: {
    color: "#5b918c",
    fontWeight: "bold",
    cursor: "pointer"
  }
};
