import { useNavigate } from "react-router-dom";

export default function Sidebar({ open, onClose }) {
  const navigate = useNavigate();

  return (
    <>
      {/* ✅ BACKDROP ONLY SHOWS WHEN OPEN */}
      {open && (
        <div
          onClick={onClose}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(0,0,0,0.35)",
            zIndex: 998,
          }}
        />
      )}

      {/* ✅ SIDEBAR ITSELF (FULLY OFFSCREEN WHEN CLOSED) */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: open ? 0 : "-280px",   // ✅ COMPLETELY GONE
          width: 260,
          height: "100vh",
          background: "#5b9491",
          color: "white",
          padding: "20px 16px",
          transition: "left 0.3s ease",
          zIndex: 999,

          display: "flex",
          flexDirection: "column",
        }}
      >
        {/* ✅ MAIN MENU */}
        <div>
          <h2 style={{ marginBottom: 20 }}>Menu</h2>

          <button onClick={() => {navigate("/"); onClose();}} style={btnStyle}>Home</button>
          <button onClick={() => {navigate("/difficulty"); onClose();}} style={btnStyle}>Play Game</button>
          <button onClick={() => {navigate("/leaderboard"); onClose();}} style={btnStyle}>Leaderboard</button>
        </div>

        {/* ✅ LOGOUT FIXED TO BOTTOM */}
        <div style={{ marginTop: "auto" }}>
          <button style={logoutStyle}>Logout</button>
        </div>
      </div>
    </>
  );
}

const btnStyle = {
  width: "100%",
  padding: 12,
  marginBottom: 12,
  background: "white",
  color: "#5b9491",
  border: "none",
  borderRadius: 6,
  fontWeight: "bold",
  cursor: "pointer",
};

const logoutStyle = {
  width: "100%",
  padding: 12,
  background: "#ffffff",
  color: "#5b9491",
  border: "none",
  borderRadius: 6,
  fontWeight: "bold",
  cursor: "pointer",
};
