      { title: "Multi-Player", text: "Compete with other players in real time", icon: "ðŸ‘¥" }
    ];

    return (
      <div style={styles.container}>
        {cards.map((card, i) => (
          <div key={i} style={styles.card}>
            <div style={styles.icon}>{card.icon}</div>
            <h3>{card.title.toUpperCase()}</h3>
            <p>{card.text}</p>
          </div>
        ))}
      </div>
    );
  }

