export default function Sidebar({ open, onClose }) {
    return (
      <>
        {/* ✅ Dark overlay ONLY when open */}
        {open && (
          <div
            onClick={onClose}
            style={{
              position: "fixed",
              inset: 0,
              background: "rgba(0,0,0,0.35)",
              zIndex: 999,
            }}
          />
        )}

        {/* ✅ Slideout menu (off-screen when closed) */}
        <div
          style={{
            position: "fixed",
            top: 0,
            left: open ? "0px" : "-280px",  // ✅ COMPLETELY HIDDEN
            width: "260px",
            height: "100vh",
            background: "#5b9491",
            color: "white",
            padding: 20,
            transition: "left 0.25s ease",
            zIndex: 1000,
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between",
          }}
        >
          <div>
            <h2 style={{ marginBottom: 20 }}>Menu</h2>
            <button style={btnStyle}>Home</button>
            <button style={btnStyle}>Play Game</button>
            <button style={btnStyle}>Leaderboard</button>
          </div>

          <button style={logoutStyle}>Logout</button>
        </div>
      </>
    );
  }

  const btnStyle = {
    width: "100%",
    padding: 12,
    marginBottom: 10,
    background: "white",
    color: "#5b9491",
    border: "none",
    borderRadius: 6,
    fontWeight: "bold",
  };

  const logoutStyle = {
    width: "100%",
    padding: 12,
    background: "#ffffff",
    color: "#5b9491",
    border: "none",
    borderRadius: 6,
    fontWeight: "bold",
  };
